import React from "react";

import ListItem from "./ListItem";
import {ListWrapper, Button} from "./Styled";

const List = ({ list,total, user,onLoadMore, hasNextPage }) => {
  return (
    <ListWrapper >
      {list.length === 0 ? (
        <p>No repositories found</p>
      ) : (
        <div>
          <p>There are {total} of {user}</p>
          {list.map((repo, index) => (
            <ListItem key={index} repo={repo.node} />
          ))}
          {hasNextPage && (
            <div>
              <Button type="submit" onClick={() => onLoadMore()} >next page</Button>
            </div>
          )}  
        </div>
      )}
    </ListWrapper>
  );
};

export default List;
