import React from "react";

import ListItem from "./ListItem";
import {ListWrapper, Button} from "./Styled";

const List = ({ list,onLoadMore, hasNextPage }) => {
  return (
    <ListWrapper >
      {list.length === 0 ? (
        <p>No repositories found</p>
      ) : (
        <>
          {list.map((repo, index) => (
            <ListItem key={index} repository={repo.node} />
          ))}
        {hasNextPage && (
            <div>
              <Button type="submit" onClick={() => onLoadMore()} >next page</Button>
            </div>
          )}  
        </>
      )}
    </ListWrapper>
  );
};

export default List;
