import React, { useState } from "react";

import { gql, useQuery } from "@apollo/client";

import List from "./List";
import SearchBar from "./SearchBar";

import ErrorMessage from "./error/ErrorMessage";

const GET_REPOSITORIES_LIST = gql`
  query($username: String! $cursor: String ) {
    user( login: $username) {
      repositories(first: 10, after: $cursor) {
        edges{
          node {
            ... on Repository {
              name
              url
              description
              updatedAt
            }
          }  
        }
        pageInfo {
          hasNextPage
          endCursor
        }
      }
    }
  }
`;

const SearchPage = () => {
  const [userName, setUserName] = useState("");
  const { loading, error, data, fetchMore } = useQuery(GET_REPOSITORIES_LIST, {
    variables: { username: userName },
  });

  const handleSearch = (str) => {
    setUserName(str);
    fetchMore({
      variables: {
        username: str,
      },
      updateQuery: (prev, { fetchMoreResult }) => {
        if (!fetchMoreResult) return prev;
        return fetchMoreResult;
      },
    });
  };

  const handleMore = () => {
    fetchMore({
      variables: {
        username: userName,
        cursor: data.user.repositories.pageInfo.endCursor,
      },
      updateQuery: (prev, { fetchMoreResult }) => {
        if (!fetchMoreResult) return prev;
        return Object.assign({}, prev, {
          ...fetchMoreResult,
          user: {
            ...fetchMoreResult.user,
            edges: [...prev.user.repositories.edges, ...fetchMoreResult.user.repositories.edges],
          },
        });
      },
    });
  };

  return (
    <div>
      {error && <ErrorMessage error={error} />}
      {!loading && <SearchBar onSearch={handleSearch} />}
      {data && (
        <List
          list={data.user.repositories.edges}
          onLoadMore={handleMore}
          hasNextPage={data.user.repositories.pageInfo.hasNextPage}
        />
      )}
    </div>
  );
};

export default SearchPage;
